import{_ as e,c as o,o as r,a as n}from"./app.6667cd43.js";const _=JSON.parse('{"title":"Chrome extensions \u6982\u51B5","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4EC0\u4E48\u662F Chrome extensions","slug":"\u4EC0\u4E48\u662F-chrome-extensions","link":"#\u4EC0\u4E48\u662F-chrome-extensions","children":[]},{"level":2,"title":"Chrome extensions \u80FD\u5E72\u4EC0\u4E48","slug":"chrome-extensions-\u80FD\u5E72\u4EC0\u4E48","link":"#chrome-extensions-\u80FD\u5E72\u4EC0\u4E48","children":[]}],"relativePath":"about/index.md"}'),t={name:"about/index.md"},s=n('<h1 id="chrome-extensions-\u6982\u51B5" tabindex="-1">Chrome extensions \u6982\u51B5 <a class="header-anchor" href="#chrome-extensions-\u6982\u51B5" aria-hidden="true">#</a></h1><h2 id="\u4EC0\u4E48\u662F-chrome-extensions" tabindex="-1">\u4EC0\u4E48\u662F Chrome extensions <a class="header-anchor" href="#\u4EC0\u4E48\u662F-chrome-extensions" aria-hidden="true">#</a></h2><p>\u6D4F\u89C8\u5668\u6269\u5C55\u7A0B\u5E8F extensions \u662F\u4E00\u4E2A\u5C0F\u578B\u7684\u7A0B\u5E8F\uFF0C\u5B83\u53EF\u4EE5\u4F7F\u7528<a href="https://developer.chrome.com/docs/extensions/reference/" target="_blank" rel="noreferrer">\u6D4F\u89C8\u5668\u63D0\u4F9B\u7684 API</a>\xA0\u548C web \u6280\u672F\u4EE5\u589E\u5F3A\u6D4F\u89C8\u5668\u529F\u80FD\u3002\u5B83\u4EEC\u8BA9\u7528\u6237\u53EF\u4EE5\u901A\u8FC7\u591A\u79CD\u65B9\u5F0F\u5B9A\u5236 <code>Chrome</code> \u7684\u529F\u80FD\u548C\u884C\u4E3A\uFF0C\u4F8B\u5982\u751F\u4EA7\u529B\u5DE5\u5177\u3001\u4FE1\u606F\u805A\u5408\u3001 \u4E30\u5BCC\u7F51\u9875\u5185\u5BB9\u7B49</p><p>\u5B98\u65B9\u8BF4\u660E\uFF1A</p><blockquote><p>Extensions are made of different, but cohesive, components. Components can include\xA0<a href="https://developer.chrome.com/docs/extensions/mv3/background_pages/" target="_blank" rel="noreferrer">background scripts</a>,\xA0<a href="https://developer.chrome.com/docs/extensions/mv3/content_scripts/" target="_blank" rel="noreferrer">content scripts</a>, an\xA0<a href="https://developer.chrome.com/docs/extensions/mv3/options/" target="_blank" rel="noreferrer">options page</a>,\xA0<a href="https://developer.chrome.com/docs/extensions/mv3/user_interface/" target="_blank" rel="noreferrer">UI elements</a>\xA0and various logic files. Extension components are created with web development technologies: HTML, CSS, and JavaScript.. \u6269\u5C55\u7531\u4E0D\u540C\u4F46\u6709\u51DD\u805A\u529B\u7684\u7EC4\u4EF6\u7EC4\u6210\u3002\u7EC4\u4EF6\u53EF\u4EE5\u5305\u62EC\u80CC\u666F\u811A\u672C\u3001\u5185\u5BB9\u811A\u672C\u3001\u9009\u9879\u9875\u9762\u3001UI \u5143\u7D20\u548C\u5404\u79CD\u903B\u8F91\u6587\u4EF6\u3002\u6269\u5C55\u7EC4\u4EF6\u662F\u4F7F\u7528 Web \u5F00\u53D1\u6280\u672F\u521B\u5EFA\u7684\uFF1AHTML\u3001CSS \u548C JavaScript\u3002</p></blockquote><p>Chrome\u63D2\u4EF6\u5B98\u65B9\u79F0\u4E3AChrome\u6269\u5C55(<code>Chrome Extension</code>)\uFF0C\u771F\u6B63\u610F\u4E49\u4E0A\u7684Chrome\u63D2\u4EF6\u662F\u66F4\u5E95\u5C42\u7684\u6D4F\u89C8\u5668\u529F\u80FD\u6269\u5C55\u3002\u672C\u6587\u9075\u5FAA\u5B98\u65B9\u6587\u6863\uFF0C\u79F0\u4E3AChrome\u6269\u5C55\u3002</p><h2 id="chrome-extensions-\u80FD\u5E72\u4EC0\u4E48" tabindex="-1">Chrome extensions \u80FD\u5E72\u4EC0\u4E48 <a class="header-anchor" href="#chrome-extensions-\u80FD\u5E72\u4EC0\u4E48" aria-hidden="true">#</a></h2><p>\u589E\u5F3A\u6D4F\u89C8\u5668\u529F\u80FD\uFF0C\u8F7B\u677E\u5B9E\u73B0\u5C5E\u4E8E\u81EA\u5DF1\u7684\u201C\u5B9A\u5236\u7248\u201D\u6D4F\u89C8\u5668\u3002</p><p>\u53C2\u8003\u5B98\u65B9\u6587\u6863\uFF1A<a href="https://developer.chrome.com/docs/extensions/mv3/devguide/" target="_blank" rel="noreferrer">Extension development overview</a> Chrome\u63D2\u4EF6\u63D0\u4F9B\u4E86\u5F88\u591A\u5B9E\u7528API\u4F9B\u6211\u4EEC\u4F7F\u7528\uFF0C\u5305\u62EC\u4F46\u4E0D\u9650\u4E8E\uFF1A</p><ul><li>\u4E66\u7B7E\u63A7\u5236\uFF1B</li><li>\u81EA\u5B9A\u4E49\u6269\u5C55\u7528\u6237\u754C\u9762</li><li>\u4E0B\u8F7D\u63A7\u5236\uFF1B</li><li>\u7A97\u53E3\u63A7\u5236\uFF1B</li><li>\u6807\u7B7E\u63A7\u5236\uFF1B</li><li>\u7F51\u7EDC\u8BF7\u6C42\u63A7\u5236\uFF0C\u5404\u7C7B\u4E8B\u4EF6\u76D1\u542C\uFF1B</li><li>\u81EA\u5B9A\u4E49\u539F\u751F\u83DC\u5355\uFF1B</li><li>\u5B8C\u5584\u7684\u901A\u4FE1\u673A\u5236\uFF1B</li></ul><p>\u8BF7\u8BBF\u95EE<a href="https://chrome.google.com/webstore/category/extensions" target="_blank" rel="noreferrer">Chrome \u5E94\u7528\u5546\u5E97</a>\u67E5\u770B\u5DF2\u53D1\u5E03\u6269\u5C55\u7A0B\u5E8F\u793A\u4F8B\u3002</p><p><img src="https://p6-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/314e0a29e5084fbb9245ad68892da313~tplv-k3u1fbpfcp-watermark.image?" alt="\u622A\u5C4F2022-09-28 15.09.13.png"></p>',12),a=[s];function i(c,h,l,m,d,p){return r(),o("div",null,a)}const f=e(t,[["render",i]]);export{_ as __pageData,f as default};
